import React, { Component } from 'react';
import './components.scss';

class Issues extends Component {
  createIssue(item) {
    return (
    <li class="issue-item" key={item.key}>
      <div className="row">
        <div className="col-1">
          <input type="checkbox" className="form-check-input" value={item.key}></input>
        </div>
        <div className="col-9 issue-text">
          <p>{item.text}</p>
        </div>
        <div className="col-2 delete-issue">
          <a onClick={() => this.deleteIssue(item.key)} className="no-decoration">
            <span class="fas fa-times"></span>
          </a>
        </div>
      </div>
    </li>
    )
  }

  deleteIssue(key) {
    this.props.delete(key);
  }

  render() {
    let todoEntries = this.props.entries;
    let itens = todoEntries.map(this.createIssue);

    return (
      <div className="issue-box">
        <div className="container">
          <div className="row">
            <div className="col-12">
              <ul className="theList">
                {itens}
              </ul>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Issues;
